mapper.delete:
DELETE FROM app_user_path

	id = #{id}
	AND user_id = #{user_id}

mapper.update:
update app_user_path

	description = #{description},
	name = #{name},
	origin = #{origin},
	destination = #{destination}


	id = #{id}
	AND user_id = #{user_id}

mapper.select:
SELECT id,

FROM app_user_path

	id = #{id}
	AND user_id = #{user_id}

SELECT id,

FROM app_user_path
WHERE user_id = #{user_id}
