mapper.delete:
DELETE FROM app_user_site

	id = #{id}
	AND user_id = #{user_id}

mapper.update:
UPDATE app_user_site

	description = #{description},
	name = #{name},
	longitude = #{longitude},
	latitude = #{latitude},
	contacts_name = #{contacts_name},
	contacts_phone = #{contacts_phone}


	id = #{id}
	AND user_id = #{user_id}

mapper.select:
SELECT id,

FROM app_user_site

	id = #{id}
	AND user_id = #{user_id}

SELECT id,

FROM app_user_site
WHERE user_id = #{user_id}
