mapper.delete:
DELETE FROM app_user_site

	id = #{id}
	AND phone = #{phone}

mapper.update:
UPDATE app_user_site
description = #{description}, name = #{name}, longitude = #{longitude}, latitude = #{latitude}, contacts_name = #{contacts_name}, contacts_phone = #{contacts_phone}
WHERE id = #{id}

mapper.select:
SELECT id,

FROM app_user_site
WHERE id = #{id}

SELECT id,

FROM app_user_site
WHERE phone = #{phone}
